import TodoList from "./components/TodoList";
import './App.css';

class App extends Component() {
  constructor(){
    super()
    this.state = {
      items: [],
      currentItem: {text: '', key: ''}
    }
  }
  handleInput = e => {
    console.log('Hello Input')
  }
  addItem = () => {
    console.log('Hello Add Item')
  }
  render() {
    return (
      <div className="App">
        <TodoList ddItem= {this.addItem}/>
      </div>
    )
  }
}

export default App;
